import React from 'react';
import styles from './Styles/Search.css';

class Search extends React.Component {
  state = {
    type: 'artist',
    artist: '',
    album: '',
    showResults: true,
  };
  SearchArtist = async (e) => {
    this.setState({
      artist: e.target.value,
      showResults: true,
    });
  };

  SearchAlbum = async (e) => {
    this.setState({
      album: e.target.value,
      showResults: true,
    });
  };

  componentDidUpdate() {
    console.log(this.state.type);
  }
  render() {
    return (
      <div>
        <form onSubmit={this.props.getArtist} className="input-group mb-3 ">
          <select>
            <option onChange={() => this.setState({ type: 'artist' })}>
              Artista
            </option>
            <option onChange={() => this.setState({ type: 'album' })}>
              √Ålbum
            </option>
          </select>
          {this.state.type}
          <input
            className={styles.input}
            type="text"
            onChange={this.SearchArtist}
            name="artist"
            placeholder="Artista"
          />
          <button className="btn btn-success">
            Pesquisa {this.state.artist}
          </button>
        </form>
      </div>
    );
  }
}

export default Search;
