import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import authenticate from '../Functions/auth';
import '../Components/Login.css';
import logo from '../Assets/logo.png';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };

  useEffect(() => {
    if (isSigned) {
      console.log('autenticado');
    }
    authenticate();
  }, [isSigned]);

  const handleEmail = (e) => {
    console.log(e.target.value);
    setEmail(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div className="wrapper">
      <img src={logo} className="img-logo" />
      {renderRegistry ? (
        <form>
          <input
            onChangeText={(e) => handleEmail(e)}
            className="input"
            placeholder="E-mail"
          ></input>
          <span>{email}</span>
          <input className="input" placeholder="Senha"></input>
          <input className="input" placeholder="Confirmar senha"></input>
          <span onClick={() => setRegistry(false)} className="registry">
            Voltar para o início
          </span>
          <button className="button-form" className="button-form">
            Cadastrar
          </button>
        </form>
      ) : (
        <form>
          <input className="input" placeholder="E-mail"></input>
          <input className="input" placeholder="Senha"></input>
          <span onClick={() => setRegistry(true)} className="registry">
            Não tem uma conta? Cadastre-se.
          </span>
          <button
            onClick={() => authenticate(email, password)}
            className="button-form"
            className="button-form"
          >
            Entrar
          </button>
        </form>
      )}
    </div>
  );
}

export default Login;
