import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import constructData from '../Functions/Auth';
import '../Components/Styles/Login.css';
import logo from '../Assets/logo.png';
import FormRegistry from '../Components/FormRegistry';
import FormLogin from '../Components/FormLogin';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [shouldRotate, setRotation] = useState('');
  const [container, setContainer] = useState(
    document.querySelector('.flip-card')
  );
  useEffect(() => {
    controlRotation();
    if (isSigned) {
      console.log('autenticado');
    }
  }, [isSigned, shouldRotate, container]);
  const controlRotation = () => {
    setRotation('-rotate');
    setContainer(`${container}${shouldRotate}`);
  };
  return (
    <div className="wrapper">
      <img src={logo} className="img-logo" />
      {/* <FormRegistry setRegistry={() => setRegistry(false)} /> */}
      <div className="flip-card">
        <div className="flip-card-inner">
          <div className="flip-card-front">
            <FormLogin
              controlRotation={() => controlRotation()}
              setRegistry={() => setRegistry(true)}
            />
          </div>
          <div className="flip-card-back">
            <FormRegistry
              controlRotation={() => controlRotation()}
              setRegistry={() => setRegistry(false)}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
