import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import '../Components/Login.css';
import logo from '../Assets/logo.png';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [rotate, setRotate] = useState('');
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };
  const authenticate = async () => {
    api.post('/auth/authenticate', params).then((response) => {
      // console.log(response.data.token);
      const token = response.data.token;
      localStorage.setItem('token', token);
      // const localToken = localStorage.getItem('token');
      api.defaults.headers.common = { Authorization: `Bearer ${token}` };
      setSigned(true);
    });
  };
  const getRotation = () => {
    const rotation = getRotateValue();
  };

  useEffect(() => {
    if (isSigned) {
      console.log('autenticado');
    }
    authenticate();
  }, [isSigned, rotate]);
  return (
    <div>
      <h1>Card Flip with Text</h1>
      <h3>Hover over the image below:</h3>

      <div className="flip-card">
        <div className={`flip-card-inner${rotate}`}>
          <div className={`flip-card-front${rotate}`}>
            <img
              onClick={() => setRotate('-rotate')}
              src="img_avatar.png"
              alt="Avatar"
            />
          </div>
          <div className="flip-card-back">
            <h1>John Doe</h1>
            <p>Architect & Engineer</p>
            <p>We love that guy</p>
          </div>
        </div>
      </div>
    </div>

    // <div className="wrapper">
    //   <img src={logo} className="img-logo" />
    //   {renderRegistry ? (
    //     <div onClick={() => setRegistry(false)}>Cadastre-se</div>
    //   ) : (
    //     <form>
    //       <input className="input" placeholder="E-mail"></input>
    //       <input className="input" placeholder="Senha"></input>
    //       <span onClick={() => setRegistry(true)} className="registry">
    //         Não tem uma conta? Cadastre-se.
    //       </span>
    //       <button className="button-form" className="button-form">
    //         Entrar
    //       </button>
    //     </form>
    //   )}
    // </div>
  );
}

export default Login;
