import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import '../Components/Login.css';
import logo from '../Assets/logo.png';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };
  const authenticate = async () => {
    api.post('/auth/authenticate', params).then((response) => {
      // console.log(response.data.token);
      const token = response.data.token;
      localStorage.setItem('token', token);
      // const localToken = localStorage.getItem('token');
      api.defaults.headers.common = { Authorization: `Bearer ${token}` };
      setSigned(true);
    });
  };
  useEffect(() => {
    if (isSigned) {
      console.log('autenticado');
    }
    authenticate();
  }, [isSigned]);
  return (
    <>
      <div class="flip-card">
        <div class="flip-card-inner">
          <div class="flip-card-front">
            <img src={logo} className="img-logo" />
            <form>
              <input className="input-email" placeholder="E-mail"></input>
              <input className="input-password" placeholder="Senha"></input>
              <button className="button-form">Entrar</button>
            </form>
          </div>
          <div class="flip-card-back">
            <h1>John Doe</h1>
            <p>Architect & Engineer</p>
            <p>We love that guy</p>
          </div>
        </div>
      </div>
      <div className="wrapper">
        <img src={logo} className="img-logo" />
        <form>
          <input placeholder="E-mail"></input>
          <input placeholder="Senha"></input>
          <button className="button-form">Entrar</button>
        </form>
      </div>
    </>
  );
}

export default Login;
