import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import '../Components/Login.css';
import logo from '../Assets/logo.png';
import $ from 'jquery';

function Login() {
  const [isSigned, setSigned] = useState(false);
  const [shouldRotate, setRotation] = useState('');
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };
  const authenticate = async () => {
    api.post('/auth/authenticate', params).then((response) => {
      // console.log(response.data.token);
      const token = response.data.token;
      localStorage.setItem('token', token);
      // const localToken = localStorage.getItem('token');
      api.defaults.headers.common = { Authorization: `Bearer ${token}` };
      setSigned(true);
    });
  };
  useEffect(() => {
    $('.button').click(function () {
      $('.flip-card').toggleClass('rotate');
      $('.flip-card-inner').toggleClass('rotate');
    });
    if (isSigned) {
      console.log('autenticado');
    }
    authenticate();
  }, [isSigned]);
  return (
    <>
      <div className="wrapper">
        <div className="flip-card">
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <img src={logo} className="img-logo" />
              <form>
                <input className="input" placeholder="E-mail"></input>
                <input className="input" placeholder="Senha"></input>
                <button
                  onClick={() => setRotation(!shouldRotate)}
                  className="button"
                >
                  Entrar
                </button>
              </form>
            </div>
            <div className="flip-card-back">
              <h1>John Doe</h1>
              <p>Architect & Engineer</p>
              <p>We love that guy</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
