import api from '../Config/api';

let token = '';
const authenticate = async (params) => {
  await api
    .post('/auth/authenticate', params)
    .then((response) => {
      const tokenReturned = response.data.token;
      api.defaults.headers.common = { Authorization: `bearer ${token}` };
      console.log(token, 'token retornado');
      token = tokenReturned;
    })
    .catch((err) => {
      console.log(err);
    });
};

const constructData = async (e, email, password) => {
  e.preventDefault();
  let data = {};
  data.email = email;
  data.password = password;
  console.log(data);
  await authenticate(data);
  return token;
};

export default constructData;
