import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import constructData from '../Functions/auth';
import '../Components/Styles/Login.css';
import logo from '../Assets/logo.png';
import FormRegistry from '../Components/FormRegistry';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [data, setData] = useState({});
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };

  useEffect(() => {
    if (isSigned) {
      console.log('autenticado');
    }
    // authenticate();
  }, [isSigned, renderRegistry]);

  const handleEmail = (e) => {
    console.log(e.target.value);
    setEmail(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div className="wrapper">
      <img src={logo} className="img-logo" />
      {renderRegistry ? (
        <>
          <FormRegistry
            setEmail={handleEmail}
            setPassword={handlePassword}
            setRegistry={() => setRegistry(false)}
          />
          <div>
            {email}
            {password}
          </div>
        </>
      ) : (
        <form>
          <input
            onChange={(e) => handleEmail(e)}
            className="input"
            placeholder="E-mail"
          ></input>
          <input
            onChange={(e) => handlePassword(e)}
            className="input"
            placeholder="Senha"
          ></input>
          <span onClick={() => setRegistry(true)} className="registry">
            NÃ£o tem uma conta? Cadastre-se.
          </span>
          <button
            onClick={(e) => constructData(e, email, password)}
            className="button-form"
            className="button-form"
          >
            Entrar
          </button>
        </form>
      )}
    </div>
  );
}

export default Login;
