import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import constructData from '../Functions/Auth';
import '../Components/Styles/Login.css';
import logo from '../Assets/logo.png';
import FormRegistry from '../Components/FormRegistry';
import FormLogin from '../Components/FormLogin';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [data, setData] = useState({});
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };

  useEffect(() => {
    const container = document.querySelector('wrapper');

    if (isSigned) {
      console.log('autenticado');
    }
  }, [isSigned, renderRegistry]);

  return (
    <div className="wrapper">
      <img src={logo} className="img-logo" />
      {renderRegistry ? (
        <FormRegistry setRegistry={() => setRegistry(false)} />
      ) : (
        // <FormLogin setRegistry={() => setRegistry(true)} />
        <div className="flip-card">
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <img
                src="img_avatar.png"
                alt="Avatar"
                style="width:300px;height:300px;"
              />
            </div>
            <div className="flip-card-back">
              <h1>John Doe</h1>
              <p>Architect & Engineer</p>
              <p>We love that guy</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
