import React from 'react';
import styles from './Styles/Search.css';

class Search extends React.Component {
  state = {
    type: 'Artista',
    artist: '',
    album: '',
    showResults: true,
  };
  SearchArtist = async (e) => {
    this.setState({
      artist: e.target.value,
      showResults: true,
    });
  };

  SearchAlbum = async (e) => {
    this.setState({
      album: e.target.value,
      showResults: true,
    });
  };

  componentDidUpdate() {
    console.log(this.state.type);
  }
  render() {
    return (
      <div>
        <form
          onSubmit={
            this.state.type === 'Artista'
              ? this.props.getArtist
              : this.props.getAlbum
          }
          className="input-group mb-3 "
        >
          <select
            style={{ height: 50 }}
            className="input-search"
            onChange={(e) => this.setState({ type: e.target.value })}
          >
            <option>Artista</option>
            <option>Álbum</option>
          </select>
          {this.state.type === 'Artista' ? (
            <div>
              <input
                className="input-search"
                type="text"
                onChange={this.SearchArtist}
                name="artist"
                placeholder="Artista"
              />
              <button
                style={{
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  height: 50,
                  width: 120,
                  textAlign: 'center',
                  paddingBottom: 10,
                }}
                className="btn btn-success button-form"
              >
                Pesquisar artista
              </button>
            </div>
          ) : (
            <div>
              <input
                className="input"
                type="text"
                onChange={this.SearchAlbum}
                name="album"
                placeholder="Álbum"
              />
              <button
                style={{
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  height: 50,
                  width: 120,
                  textAlign: 'center',
                  paddingBottom: 10,
                }}
                className="btn btn-success"
              >
                Pesquisar álbum
              </button>
            </div>
          )}
          {/* <input
            className={styles.input}
            type="text"
            onChange={this.SearchArtist}
            name="artist"
            placeholder="Artista"
          />
          <button className="btn btn-success">
            Pesquisa {this.state.artist}
          </button> */}
        </form>
      </div>
    );
  }
}

export default Search;
