import React, { useEffect, useState } from 'react';
import api from '../Config/api';
import constructData from '../Functions/Auth';
import '../Components/Styles/Login.css';
import logo from '../Assets/logo.png';
import FormRegistry from '../Components/FormRegistry';
import FormLogin from '../Components/FormLogin';
function Login() {
  const [isSigned, setSigned] = useState(false);
  const [renderRegistry, setRegistry] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [data, setData] = useState({});
  const params = {
    email: 'love.puzzling@gmail.com',
    password: '123456',
  };

  useEffect(() => {
    if (isSigned) {
      console.log('autenticado');
    }
    // authenticate();
  }, [isSigned, renderRegistry]);

  const handleEmail = (e) => {
    console.log(e.target.value);
    setEmail(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div className="wrapper">
      <img src={logo} className="img-logo" />
      {renderRegistry ? (
        <FormRegistry />
      ) : (
        // <form>
        //   <input
        //     onChange={(e) => handleEmail(e)}
        //     className="input"
        //     placeholder="E-mail"
        //   ></input>
        //   <input
        //     onChange={(e) => handlePassword(e)}
        //     className="input"
        //     placeholder="Senha"
        //   ></input>
        //   <span onClick={() => setRegistry(true)} className="registry">
        //     Não tem uma conta? Cadastre-se.
        //   </span>
        //   <button
        //     onClick={(e) => constructData(e, email, password)}
        //     className="button-form"
        //     className="button-form"
        //   >
        //     Entrar
        //   </button>
        // </form>
        <FormLogin setRegistry={() => setRegistry(true)} />
      )}
    </div>
  );
}

export default Login;
